catalog_script_client.script:
function onChange(control, oldValue, newValue, isLoading) {
  if (isLoading || newValue === '') {
    return;
  }

  var fd = 'what_is_the_request';

	// Adiciona todas as opções no what_is_the_request
	// addOption(field: str, value: str, label: str)
	(function addAllOptions() {
	g_form.addOption(
      fd,
      'issuance_of_invoice_out_of_the_country',
      'Emissão de Nota Fiscal para fora do país'
    );
    g_form.addOption(
      fd,
      'issuance_of_invoice_to_other_states',
      'Emissão de Nota Fiscal para outros estados'
    );
    g_form.addOption(
      fd,
      'unidentified_request_at_pdv',
      'Pedido não identificado no PDV'
    );
    g_form.addOption(
      fd,
      'return_or_cancellation_of_sale_on_ticket',
      'Devolução ou cancelamento de venda em boleto'
    );
  })();

	// remove as opções
	// caso seja x, remova (y, z)
	// removeOption(field: str, value: str)
  switch (newValue) {
    case 'invoice_issuance':
      g_form.removeOption(fd, 'return_or_cancellation_of_sale_on_ticket');
      g_form.removeOption(fd, 'unidentified_request_at_pdv');
      break;
    case 'telesales_request':
      g_form.removeOption(fd, 'return_or_cancellation_of_sale_on_ticket');
      g_form.removeOption(fd, 'issuance_of_invoice_to_other_states');
      g_form.removeOption(fd, 'issuance_of_invoice_out_of_the_country');
      break;
    case 'billet':
      g_form.removeOption(fd, 'issuance_of_invoice_to_other_states');
      g_form.removeOption(fd, 'issuance_of_invoice_out_of_the_country');
      g_form.removeOption(fd, 'unidentified_request_at_pdv');
      break;
	// remove todas, caso seja escolhido -- None -- no what_do_you_want
    default:
      break;
  }
}
