sys_script_include.script:
var FipeProvider = Class.create();
FipeProvider.prototype = {
    initialize: function() {
    },

	getMarcas: function() {
		var resp;

		try {
			var r = new sn_ws.RESTMessageV2('Fipe','getMarcas');

			var response = r.execute();
			var request = r.getRequestBody();
			var status = response.getStatusCode();
			resp = {
				payload: request,
				body: response.getBody(),
				status: status
			};
			var responseBody = response.getBody();
			var httpStatus = response.getStatusCode();
		}
		catch(ex) {
			var message = ex.message;
			resp = {
				payload: request,
				body: message,
				status: status
			};
		}
		return resp;
	},

   type: 'FipeProvider'
};
